mapper.select:
SELECT

FROM tds_account_bank

	and id = #{id}
	and less_name = #{lessName}
	and account_name = #{accountName}
	and bank_type = #{bankType}
	and bank_name = #{bankName}
	and open_account = #{openAccount}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and creater = #{creater}
	and updater = #{updater}
	and remarks = #{remarks}
	and uid = #{uid}
	and is_deleted ='0'

mapper.update:
UPDATE tds_account_bank
SET IS_DELETED='1'
WHERE id=#{id}
