mapper.select:
SELECT

FROM tds_approval_log

	and id = #{id}
	and user_id = #{userId}
	and app_status = #{appStatus}
	and app_remarks = #{appRemarks}
	and spare1 = #{spare1}
	and create_time = #{createTime}
	and is_deleted = '0'

mapper.update:
UPDATE tds_approval_log
SET IS_DELETED='1'
WHERE id=#{id}

UPDATE tds_approval_log
id = #{id}, user_id = #{userId}, app_status = #{appStatus}, app_remarks = #{appRemarks}, spare1 = #{spare1}, is_deleted = #{isDeleted}, create_time = #{createTime},
WHERE id=#{id}

mapper.select:
	SELECT u.id as userId,u.name as userName,d.id as departId, d.name as
	departName,u.contact,u.phone,u.create_time,
	u.source,com.com_name as
	comName,com.com_url as
	comUrl,u.is_deleted,u.parent_user_id from
	tds_user u LEFT JOIN tds_user_department ud
	ON u.id=ud.user_id LEFT JOIN tds_department d
	on d.id=ud.depart_id LEFT JOIN tds_company com ON
	com.id=u.com_id where u.id in (
	select id from tds_user
	where parent_user_id=#{parentUserId}
	and is_deleted='2'
	)
	AND d.id = #{departId}
	AND u.name = #{userName}
	
		AND u.create_time >= #{statTime}
		AND u.create_time <= #{endTime}
	
    order by u.create_time desc
	LIMIT #{pageNumber},#{numPerPage}

SELECT count(1) from tds_user u LEFT JOIN tds_user_department ud
ON u.id=ud.user_id  LEFT JOIN tds_department d
on d.id=ud.depart_id LEFT JOIN tds_company com  ON
com.id=u.com_id  where  u.id in (
select id from tds_user 
where parent_user_id=#{parentUserId} 
 and is_deleted='2'
 )
AND d.id = #{departId}
AND u.contact = #{contact}

	AND u.create_time >= #{statTime}
	AND u.create_time <= #{endTime}
