mapper.select:
SELECT

FROM tds_attorn_log

	and id = #{id}
	and user_id = #{userId}
	and att_remarks = #{attRemarks}
	and create_time = #{createTime}
	and spare1 = #{spare1}
	and attorn_user_id = #{attornUserId}
	and attorn_user_name = #{attornUserName}
  and is_deleted ='0'

mapper.update:
UPDATE tds_attorn_log
user_id = #{userId}, att_remarks = #{attRemarks}, is_deleted = #{isDeleted}, create_time = #{createTime}, spare1 = #{spare1}, attorn_user_id = #{attornUserId}, attorn_user_name = #{attornUserName},
WHERE id=#{id}
