mapper.select:
SELECT

FROM tds_carry

	and id = #{id}
	and user_id = #{userId}
	and carry_serial = #{carrySerial}
	and carry_order = #{carryOrder}
	and carry_type = #{carryType}
	and carry_type_name = #{carryTypeName}
	and carr_money = #{carrMoney}
	and carr_status = #{carrStatus}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and creater = #{creater}
	and updater = #{updater}
	and remarks = #{remarks}
	and is_deleted = '0'

mapper.update:
UPDATE tds_carry
id = #{id}, user_id = #{userId}, carry_serial = #{carrySerial}, carry_order = #{carryOrder}, carry_type = #{carryType}, carry_type_name = #{carryTypeName}, is_deleted = #{isDeleted}, carr_money = #{carrMoney}, carr_status = #{carrStatus}, create_time = #{createTime}, update_time = #{updateTime}, creater = #{creater}, updater = #{updater}, remarks = #{remarks},
WHERE id=#{id}

mapper.select:
SELECT

FROM tds_carry

	
		AND create_time >= #{statTime}
		AND create_time <= #{endTime}
	
	
		and carr_status=#{carrStatus}
	
	and is_deleted ='0'

ORDER BY create_time DESC
LIMIT #{pageNumber},#{numPerPage}

SELECT count(1)
FROM tds_carry

	
		AND create_time >= #{statTime}
		AND create_time <= #{endTime}
	
	
		and carr_status=#{carrStatus}
	
	and is_deleted ='0'
