mapper.select:
SELECT

FROM tds_commission

	and id = #{id}
	and user_id = #{userId}
	and serial_number = #{serialNumber}
	and order_number = #{orderNumber}
	and serial_money = #{serialMoney}
	and before_money = #{beforeMoney}
	and comm_status = #{commStatus}
	and create_time = #{createTime}
	and creater = #{creater}
	and update_time = #{updateTime}
	and updater = #{updater}
	and is_deleted ='0'

mapper.update:
UPDATE tds_commission SET
IS_DELETED='1'
WHERE id=#{id}

mapper.select:
SELECT

FROM tds_commission

	AND comm_status = #{commStatus}
	AND create_time >= #{statTime}
	AND create_time <= #{endTime}
	AND is_deleted ='0' order by create_time desc

LIMIT #{pageNumber},#{numPerPage}

SELECT count(1) FROM tds_commission

	AND comm_status = #{commStatus}
	AND create_time >= #{statTime}
	AND create_time <= #{endTime}
	AND is_deleted ='0'
