mapper.select:
	select u.id as userId,u.name as userName,d.id as departId, d.name as
	departName,u.contact,u.phone,u.create_time,
	u.source,com.com_url as
	comUrl,u.is_deleted
	from tds_user u,
	tds_user_department
	ud,tds_department d,
	tds_company com
	where u.id=ud.user_id and
	d.id=ud.depart_id and com.id=u.com_id
	and d.parent_id=2

		AND d.id = #{departId}
		AND u.contact = #{contact}
		
			AND u.create_time >= #{statTime}
			AND u.create_time <= #{endTime}
		
		AND u.is_deleted ='0' order by u.create_time desc

	LIMIT #{pageNumber},#{numPerPage}

	select count(1)
	from tds_user u,
	tds_user_department ud,tds_department
	d,
	tds_company com
	where u.id=ud.user_id and d.id=ud.depart_id and
	com.id=u.com_id
	and d.parent_id=2
		AND d.id = #{departId}
		AND u.contact = #{contact}
		
			AND u.create_time >= #{statTime}
			AND u.create_time <= #{endTime}
		
		AND u.is_deleted ='0' order by u.create_time desc
