mapper.select:
SELECT

FROM tds_department

	and id = #{id}
	and Name = #{name},
	and remarks = #{remarks}
	andparent_id = #{parentId}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and creater = #{creater}
	and updater = #{updater}
	and is_deleted ='0'

mapper.update:
UPDATE tds_department SET
IS_DELETED=1
WHERE ID=#{id}

UPDATE tds_department
Name = #{name}, remarks = #{remarks}, parent_id = #{parentId}, create_time = #{createTime}, update_time = #{updateTime}, creater = #{creater}, updater = #{updater}, is_deleted = #{isDeleted},
WHERE id=#{id}

mapper.select:
select u.id,u.contact,r.role_name,d.name,u.com_name,u.com_url,
u.create_time,u.source,u.is_deleted
from tds_user u inner join tds_user_role ur
on u.id=ur.user_id inner join tds_role r
on r.id=ur.role_id inner join tds_user_department ud
on ud.user_id=u.id inner join tds_department d
on d.id=ud.depart_id

	AND r.role_name = #{role_name}
	AND d.name = #{name}
	AND u.contact = #{contact}
	AND u.create_time = #{create_time}
	AND u.is_deleted ='0' order by u.create_time desc

LIMIT #{currentPage},#{numPerPage}

		SELECT * FROM tds_function
		WHERE id IN (
		SELECT fr.fun_id FROM tds_function_role fr
		WHERE fr.role_id IN (
		SELECT tur.role_id FROM tds_user_role tur
		WHERE tur.user_id =#{userId}
		AND is_deleted = '0'
		) AND is_deleted = '0'

		) AND is_deleted = '0';
