mapper.select:
SELECT

FROM tds_money_approval_back

	and id = #{id}
	and user_id = #{userId}
	and user_name = #{userName}
	and serial_number = #{serialNumber}
	and order_number = #{orderNumber}
	and approval_status = #{approvalStatus}
	and go_order_number = #{goOrderNumber}
	and go_sum_money = #{goSumMoney}
	and go_pname = #{goPname}
	and go_pname_number = #{goPnameNumber}
	and back_pname_number = #{backPnameNumber}
	and back_sum_money = #{backSumMoney}
	and back_remarks = #{backRemarks}
	and plus_number = #{plusNumber}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and creater = #{creater}
	and updater = #{updater}
	and is_deleted = '0'

mapper.update:
UPDATE tds_money_approval_back
user_id = #{userId}, user_name = #{userName}, serial_number = #{serialNumber}, order_number = #{orderNumber}, approval_status = #{approvalStatus}, go_order_number = #{goOrderNumber}, go_sum_money = #{goSumMoney}, go_pname = #{goPname}, go_pname_number = #{goPnameNumber}, back_pname_number = #{backPnameNumber}, back_sum_money = #{backSumMoney}, back_remarks = #{backRemarks}, plus_number = #{plusNumber}, is_deleted = #{isDeleted}, create_time = #{createTime}, update_time = #{updateTime}, creater = #{creater}, updater = #{updater},
WHERE id=#{id}

mapper.select:
SELECT * FROM tds_money_approval_back WHERE is_deleted='0' AND approval_status = #{approvalStatus} AND create_time >= #{startOrderTime} AND create_time <= #{endOrderTime} AND user_name like concat(concat('%',#{likeName}),'%') AND go_pname like concat(concat('%',#{likeName}),'%') AND id like concat(concat('%',#{likeName}),'%') AND go_order_number like concat(concat('%',#{likeName}),'%')
AND user_name like concat(concat('%',#{likeName}),'%') or go_pname like concat(concat('%',#{likeName}),'%') or id like concat(concat('%',#{likeName}),'%') or go_order_number like concat(concat('%',#{likeName}),'%')
order by create_time desc LIMIT #{pageNumber},#{numPerPage}
