mapper.select:
SELECT

FROM tds_money_approval_back

	and id = #{id}
	and user_id = #{userId}
	and plus_number = #{plusNumber}
	and user_name = #{userName}
	and serial_number = #{serialNumber}
	and order_number = #{orderNumber}
	and serual_money = #{serualMoney}
	and back_number = #{backNumber}
	and back_money = #{backMoney}
	and back_number_commission = #{backNumberCommission}
	and approval_status = #{approvalStatus}
	and back_remarks = #{backRemarks}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and creater = #{creater}
	and updater = #{updater}
	and back_pname = #{backPname}
	and remarks1 = #{remarks1}
	and is_deleted ='0'

SELECT * FROM tds_money_approval_back WHERE is_deleted='0' AND approval_status = #{approvalStatus} AND create_time >= #{startOrderTime} AND create_time <= #{endOrderTime} AND user_name like concat(concat('%',#{likeName}),'%') AND back_pname like concat(concat('%',#{likeName}),'%') AND id like concat(concat('%',#{likeName}),'%') AND order_number like concat(concat('%',#{likeName}),'%')
AND user_name like concat(concat('%',#{likeName}),'%') or back_pname like concat(concat('%',#{likeName}),'%') or id like concat(concat('%',#{likeName}),'%') or order_number like concat(concat('%',#{likeName}),'%')
order by create_time desc LIMIT #{pageNumber},#{numPerPage}
