mapper.select:
SELECT

FROM tds_money_approval

	and id = #{id}
	and user_id = #{userId}
	and serial_number = #{serialNumber}
	and order_number = #{orderNumber}
	and user_name = #{userName}
	and pname = #{pname}
	and number = #{number}
	and money = #{money}
	and sum_money = #{sumMoney}
	and payment = #{payment}
	and bank_id = #{bankId}
	and create_time = #{createTime}
	and arrive_time = #{arriveTime}
	and billing = #{billing}
	and approval_status = #{approvalStatus}
	and approval_type = #{approvalType}
	and remarks = #{remarks}
	and update_time = #{updateTime}
	and updater = #{updater}
	and creater = #{creater}
	and pname_id = #{pnameId}
	and commisson_money = #{commissonMoney}
	and is_deleted = '0'

mapper.update:
UPDATE tds_money_approval
SET IS_DELETED='1'
WHERE id=#{id}

mapper.select:
	SELECT *,b.less_name as lessName,app.create_time as
	createTime,enum.sl_name as pname FROM tds_money_approval
	app,tds_account_bank b,tds_enum enum
	where user_id in (
	select id from tds_user
	where
	parent_user_id=#{parentUserId}
	AND is_deleted='0'
	)and app.bank_id=b.id
	and enum.id=app.pname_id
	and app.is_deleted='0'		

	AND approval_status = #{approvalStatus}
	AND app.create_time >= #{startOrderTime}
	AND app.create_time <= #{endOrderTime}
	AND app.arrive_time >= #{startArrivTime}
	AND app.arrive_time <= #{endArrivTime}
	AND app.user_name like concat(concat('%',#{likeName}),'%')
	
	AND app.pname like concat(concat('%',#{likeName}),'%')
	
	AND b.less_name like concat(concat('%',#{likeName}),'%')
	
	
		AND app.user_name like concat(concat('%',#{likeName}),'%')
		or app.pname like concat(concat('%',#{likeName}),'%')
		or b.less_name
		like concat(concat('%',#{likeName}),'%')
	
	order by app.create_time desc
	LIMIT #{pageNumber},#{numPerPage}

	SELECT count(1) FROM tds_money_approval app,tds_account_bank b
	where
	user_id in (
	select id from tds_user
	where
	parent_user_id=#{parentUserId}
	AND
	is_deleted='0'
	)and app.bank_id=b.id
	 and app.is_deleted='0'

	AND approval_status = #{approvalStatus}
	AND app.create_time >= #{startOrderTime}
	AND app.create_time <= #{endOrderTime}
	AND app.arrive_time >= #{startArrivTime}
	AND app.arrive_time <= #{endArrivTime}
	AND app.user_name like concat(concat('%',#{likeName}),'%')
	
	AND app.pname like concat(concat('%',#{likeName}),'%')
	
	AND b.less_name like concat(concat('%',#{likeName}),'%')
	
	
		AND app.user_name like concat(concat('%',#{likeName}),'%')
		or app.pname like concat(concat('%',#{likeName}),'%')
		or b.less_name
		like concat(concat('%',#{likeName}),'%')

SELECT
approval_status FROM tds_money_approval
where
order_number=#{orderNumber}
and user_id=#{userId}
and is_deleted='0'

mapper.update:
UPDATE tds_money_approval set approval_status=#{approvalStatus}
,arrive_time=#{arriveTime}
where id=#{id} and is_deleted='0'
