mapper.select:
SELECT

FROM tds_money_approval

	and id = #{id}
	and user_id = #{userId}
	and serial_number = #{serialNumber}
	and order_number = #{orderNumber}
	and user_name = #{userName}
	and pname = #{pname}
	and number = #{number}
	and money = #{money}
	and sum_money = #{sumMoney}
	and payment = #{payment}
	and bank_id = #{bankId}
	and create_tiem = #{createTiem}
	and arrive_time = #{arriveTime}
	and billing = #{billing}
	and approval_status = #{approvalStatus}
	and approval_type = #{approvalType}
	and remarks = #{remarks}
	and update_time = #{updateTime}
	and updater = #{updater}
	and is_deleted = '0'

mapper.update:
UPDATE tds_money_approval
user_id = #{userId}, serial_number = #{serialNumber}, order_number = #{orderNumber}, user_name = #{userName}, pname = #{pname}, number = #{number}, money = #{money}, sum_money = #{sumMoney}, payment = #{payment}, bank_id = #{bankId}, create_tiem = #{createTiem}, arrive_time = #{arriveTime}, billing = #{billing}, approval_status = #{approvalStatus}, approval_type = #{approvalType}, is_deleted = #{isDeleted}, remarks = #{remarks}, update_time = #{updateTime}, updater = #{updater},
WHERE id=#{id}

UPDATE tds_money_approval  
SET approval_status=#{approvalStatus}, update_time = #{updateTime},updater = #{updater}
WHERE id=#{id}
AND order_number=#{orderNumber}
AND approval_type=#{approvalType}
AND is_deleted='0'
