mapper.select:
SELECT

FROM tds_serual_info

	and id = #{id}
	and serial_number = #{serialNumber}
	and order_number = #{orderNumber}
	and serial_type = #{serialType}
	and user_id = #{userId}
	and serial_money = #{serialMoney}
	and before_money = #{beforeMoney}
	and serial_status = #{serialStatus}
	and create_time = #{createTime}
	and creater = #{creater}
	and update_time = #{updateTime}
	and updater = #{updater}
	and is_deleted ='0'

mapper.update:
UPDATE tds_serual_info
serial_number = #{serialNumber}, order_number = #{orderNumber}, serial_type = #{serialType}, user_id = #{userId}, serial_money = #{serialMoney}, before_money = #{beforeMoney}, serial_status = #{serialStatus}, is_deleted = #{isDeleted}, create_time = #{createTime}, creater = #{creater}, update_time = #{updateTime}, updater = #{updater},
WHERE id=#{id}

mapper.select:
    SELECT
	
	FROM tds_serual_info
	
     	AND serial_type = #{serialType}
		AND serialStatus = #{serialStatus}
		AND create_time >= #{statTime}
		AND  create_time <= #{endTime}
		AND is_deleted ='0'  order by create_time desc
	
	LIMIT #{pageNumber},#{numPerPage}

  SELECT count(1)  FROM tds_serual_info
	
     	AND serial_type = #{serialType}
		AND serialStatus = #{serialStatus}
		AND create_time >= #{statTime}
		AND  create_time <= #{endTime}
		AND is_deleted ='0'
