mapper.select:
SELECT

FROM tds_user_customer

	and id = #{id}
	and user_id = #{userId}
	and last_money_time = #{lastMoneyTime}
	and sum_money = #{sumMoney}
	and sum_commission = #{sumCommission}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and overplus_commission = #{overplusCommission}
	and is_deleted ='0'

mapper.update:
UPDATE tds_user_customer
SET IS_DELETED='1'
WHERE id=#{id}

UPDATE tds_user_customer
user_id = #{userId}, last_money_time = #{lastMoneyTime}, sum_money = #{sumMoney}, sum_commission = #{sumCommission}, is_deleted = #{isDeleted}, create_time = #{createTime}, update_time = #{updateTime}, creater = #{creater}, updater = #{updater},
WHERE id=#{id}

mapper.select:
select u.id as userId,u.user_name as userName,d.id as departId, d.name as departName,u.contact,u.phone,u.create_time,u.parent_user_id, u.source,u.is_deleted,cu.last_money_time as lastMoneyTime,cu.sum_money as sumMoney,cu.sum_commission as sumCommission,com.com_url as comUrl,com.com_name as comName,d.name as departName,d.id as departId from tds_user u LEFT JOIN tds_user_customer cu on cu.user_id=u.id LEFT JOIN tds_user_department ud ON ud.user_id=u.id LEFT JOIN tds_department d on d.id=ud.depart_id LEFT JOIN tds_company com on com.id=u.com_id where u.parent_user_id=#{parentUserId} AND d.id = #{departId} AND u.contact = #{contact}
AND u.create_time >= #{statTime} AND u.create_time <= #{endTime}
AND u.is_deleted ='0' AND u.status=#{status} ORDER BY u.create_time desc LIMIT #{pageNumber},#{numPerPage}

select count(1)
from tds_user u LEFT JOIN tds_user_customer cu
on
cu.user_id=u.id LEFT JOIN tds_user_department ud ON
ud.user_id=u.id
LEFT JOIN tds_department d on d.id=ud.depart_id LEFT JOIN tds_company
com
on com.id=u.com_id
where u.parent_user_id=#{parentUserId}
AND d.id = #{departId}
AND u.contact = #{contact}

	AND u.create_time >= #{statTime}
	AND u.create_time <=
	#{endTime}

AND u.is_deleted =#{isDeleted} 
AND u.status=#{status}
ORDER BY u.create_time desc
