mapper.select:
SELECT

FROM tds_user

	and id = #{id}
	and name = #{name}
	and user_name = #{userName}
	and contact = #{contact}
	and phone = #{phone}
	and customer_type = #{customerType}
	and com_id = #{comId}
	and user_qq = #{userQq}
	and user_email = #{userEmail}
	and user_identity = #{userIdentity}
	and user_number = #{userNumber}
	and password = #{password}
	and parent_user_id = #{parentUserId}
	and hedehref = #{hedehref}
	and login_ip = #{loginIp}
	and last_login_time = #{lastLoginTime}
	and create_time = #{createTime}
	and update_time = #{updateTime}
	and status = #{status}
	and source = #{source}
	and creater = #{creater}
	and updater = #{updater}
	and remarks = #{remarks}
	and cre_user_id = #{creUserId}
	and is_deleted = '0'

mapper.update:
UPDATE tds_user
name = #{name}, user_name = #{userName}, contact = #{contact}, phone = #{phone}, customer_type = #{customerType}, com_id = #{comId}, user_qq = #{userQq}, user_email = #{userEmail}, user_identity = #{userIdentity}, user_number = #{userNumber}, password = #{password}, parent_user_id = #{parentUserId}, hedehref = #{hedehref}, login_ip = #{loginIp}, last_login_time = #{lastLoginTime}, create_time = #{createTime}, update_time = #{updateTime}, status = #{status}, source = #{source}, creater = #{creater}, updater = #{updater}, remarks = #{remarks}, is_deleted = #{isDeleted}, cre_user_id = #{creUserId},
WHERE id=#{id}

UPDATE tds_user set
password=#{newPass}
where id=#{userId}

mapper.select:
SELECT

FROM tds_user
where phone=#{name}
and password=#{password}
and
is_deleted='0'

mapper.update:
UPDATE tds_user
UPDATER = #{updater}, IS_DELETED = #{isDeleted}, LOGIN_IP = #{loginIp}, UPDATE_TIME = #{updateTime}, STATUS = #{status},
WHERE id=#{id}

UPDATE tds_user set
hedehref=#{hedehref}
where id=#{id} and is_deleted='0'
