mapper.select:
SELECT

FROM tds_user

	AND ID = #{id}
	AND NAME = #{name}
	AND COM_NAME = #{comName}
	AND COM_URL = #{comUrl}
	AND CONTACT = #{contact}
	AND PHONE = #{phone}
	AND PASSWORD = #{password}
	AND INVITEDCODE = #{invitedcode}
	AND REMARKS = #{remarks}
	AND CREATER = #{creater}
	AND UPDATER = #{updater}
	AND UPDATE_TIME = #{updateTime}
	AND CREATE_TIME = #{createTime}
	AND TOKEN = #{token}
	AND LOGIN_IP = #{loginIp}
	AND LAST_LOGIN_TIME = #{lastLoginTime}
	AND VERSION = #{version}
	AND HEDEHREF = #{hedehref}
	AND SOURCE = #{source}
	AND IS_DELETED=0

mapper.update:
UPDATE tds_user SET
IS_DELETED=1 
WHERE ID=#{id}

UPDATE tds_user
name = #{name}, COM_NAME = #{comName}, COM_URL = #{comUrl}, CONTACT = #{contact}, PHONE = #{phone}, PASSWORD = #{password}, INVITEDCODE = #{invitedcode}, REMARKS = #{remarks}, CREATER = #{creater}, UPDATER = #{updater}, IS_DELETED = #{isDeleted}, UPDATE_TIME = #{updateTime}, CREATE_TIME = #{createTime}, TOKEN = #{token}, LOGIN_IP = #{loginIp}, LAST_LOGIN_TIME = #{lastLoginTime}, VERSION = #{version}, HEDEHREF = #{hedehref}, SOURCE = #{source},
WHERE id=#{id}

