mapper.select:
SELECT

FROM tds_user_role

	 AND ID=#{id}
	 AND USER_ID=#{userId}
	 AND ROLE_ID=#{roleId}
	AND CREATE_TIME=#{createTime}
	AND UPDATE_TIME=#{updateTime}
	AND CREATER=#{creater}
	AND UPDATER=#{updater}
	AND STATUS=#{status}
	AND IS_DELETED=0

mapper.update:
UPDATE tds_user_role
USER_ID = #{userId}, ROLE_ID = #{roleId}, CREATE_TIME = #{createTime}, UPDATE_TIME = #{updateTime}, CREATER = #{creater}, UPDATER = #{updater}, IS_DELETED = #{isDeleted}, STATUS = #{status},
WHERE ID=#{id}
